import PrimerProjecto from "./1_proyecto_covid/primerprojecto"
import SegundoProjecto  from "./2_proyecto_instrumento/segundoprojecto";
import TercerProjecto from "./3_proyecto_/tercerprojecto";
import "./css/main.css";
import React, { useState } from "react";
const Main=()=>{
    const [proyecto,setproyecto]=useState("")
    function abrir(name){
    setproyecto(name)}
    

    return(
        <div className="main"> 
            <h1>Contenido principal</h1> 
            <p>Este es el contenido principal de la aplicaci√≥n.</p>
            <div className="fila">
            <button onClick={()=>abrir("primer")}>Covid</button>
            <button onClick={()=>abrir("segundo")}>Instrumento</button>
            <button onClick={()=>abrir("tercer")}>Tercer projecto</button>
            </div > 
            {proyecto === "primer" && <PrimerProjecto />}
            {proyecto === "segundo" && <SegundoProjecto />} 
            {proyecto === "tercer" && <TercerProjecto />}
            </div>
    )
}

export default Main
