import React, { useEffect } from "react";
import ".//segundoproyecto.css";
import AudioPlayer from ".//Audio";

const SegundoProyecto=()=>{
const audioP=AudioPlayer()

useEffect(
    ()=>{
        audioP.setInstrument("acoustic_grand_piano")

    },[]);

const handleClick=(note)=>{
    audioP.playNote(note)
}
return(
    <div className="fila">
        <div className="tecla" onClick={handleClick("C4")}>Do</div>
        <div className="tecla black"onClick={handleClick("C")}>Re</div>
        <div className="tecla" onClick={handleClick}>Mi</div>
        <div className="tecla black" onClick={handleClick}>Fa</div>
        <div className="tecla" onClick={handleClick}>Sol</div>
        <div className="tecla black" onClick={handleClick}>La</div>
        <div className="tecla" onClick={handleClick}>Si</div>
        <div className="tecla black" onClick={handleClick}>Do#</div>
        <div className="tecla" onClick={handleClick}>Re#</div>
        <div className="tecla" onClick={handleClick}>Mi#</div>
       
    </div>
)

}

export default SegundoProyecto
