import { useEffect } from "react";
import React, { useState } from "react";
import "./prpoy.css";
import {MapContainer,TileLayer,Popup,Marker} from 'react-leaflet'

const PrimerProyecto=()=>{
    const [data, setData] = useState({ 
        updated: 0, 
        country: "",
         countryInfo: { 
            _id: 0,
             iso2: "", 
             iso3: "", 
             lat: 0,
              long: 0, 
              flag: "" },
               cases: 0, 
               todayCases: 0, 
               deaths: 0,
                todayDeaths: 0,
                 recovered: 0,
                  todayRecovered: 0,
                   active: 0, 
                   critical: 0,
                    casesPerOneMillion: 0,
                     deathsPerOneMillion: 0,
                      tests: 0,
                       testsPerOneMillion: 0, 
                       population: 0,
                        continent: "",
                         oneCasePerPeople: 0,
                          oneDeathPerPeople: 0,
                           oneTestPerPeople: 0,
                            activePerOneMillion: 0,
                             recoveredPerOneMillion: 0,
                              criticalPerOneMillion: 0
                            });
    const [loading, setLoading] = useState(true);
    const [animate,setAnimate]=useState('carga');
    const[pais,setPais]=useState(data.country)
    const inpPais=(e)=>{return setPais(e.target.value)}


useEffect(() => { 
if(pais){
    setLoading(true)
    fetch(`https://disease.sh/v3/covid-19/countries/${pais}`) 
    .then(response => response.json()) 
    .then(data => {
        setData(data); 
        setLoading(false);
        setAnimate('carga-animate')})
    .catch(error => { 
    console.error('Error fetching data:', error);
    setLoading(false); }); 
}
},[pais]);

const diferencia=parseFloat((data.cases-data.recovered)/data.cases*100).toFixed(2);
const position = [data.countryInfo.lat, data.countryInfo.long];

if (loading) {
     return <div>Cargando...</div>;
     }
return(
    <div className={`prp ${animate ?'carga-animate':'carga'}`}>
        <h1>Estadisticas de covid</h1>
        <input  type="text" value={pais} onChange={inpPais} />
        {pais!==""&&(
        <div className="mapa">
        <MapContainer center={position} zoom={3} scrollWheelZoom={false} key={position}>
           <TileLayer attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
           <Marker position={position}>
           <Popup>
           <ol>
        <li>Casos: {data.cases}</li> 
        <li>Muertes: {data.deaths}</li> 
        <li>Recuperados: {data.recovered}</li>
        <li>Diferencia: {diferencia}% </li>
        </ol>
            </Popup>
           </Marker>
           </MapContainer>
           </div>)}
    </div>
)

}

export default PrimerProyecto;
