import React from "react";
import ".//segundoproyecto.css";
import SoundFontPlayer from "soundfont-player";
import AudioContext from "./AudioContext";

const SegundoProyecto=()=>{
    const NullSoundFontPlayerNoteAudio = {
        stop() {}
      };
      
      const NullSoundFontPlayer = {
        play() {
          return NullSoundFontPlayerNoteAudio;
        }
      };
      const AudioPlayer = () => {
        //Audio Context
        const audioContext = AudioContext && new AudioContext();
      
        //soundPlayer
        let soundPlayer = NullSoundFontPlayer;
        //setInstrument
        const Player = {
          setInstrument(instrumentName) {
            SoundFontPlayer.instrument(audioContext, instrumentName)
              .then(soundfontPlayer => {
                soundPlayer = soundfontPlayer;
              })
              .catch(e => {
                soundPlayer = NullSoundFontPlayer;
              });
          },
          playNote(note) {
            soundPlayer.play(note);
          }
        };

    }


return(
    <div className="fila">
        <div className="tecla">Do</div>
        <div className="tecla black">Re</div>
        <div className="tecla">Mi</div>
        <div className="tecla black">Fa</div>
        <div className="tecla">Sol</div>
        <div className="tecla black">La</div>
        <div className="tecla">Si</div>
        <div className="tecla black">Do#</div>
        <div className="tecla">Re#</div>
        <div className="tecla">Mi#</div>
       
    </div>
)

}

export default SegundoProyecto
