import { useEffect } from "react"; 
import React, { useState } from "react";
import "./prpoy.css";
import 'leaflet/dist/leaflet.css';
import { MapContainer, TileLayer, Popup, Marker } from 'react-leaflet';

const MapComponent = ({ pais }) => {
     const [data, setData] = useState({
         updated: 0, country: "",
          countryInfo: {
            _id: 0,
            iso2: "",
            iso3: "", 
            lat: 0, 
            long: 0, 
            flag: "" }, 
            cases: 0, 
            todayCases: 0, 
            deaths: 0, 
            todayDeaths: 0,
            recovered: 0, 
            todayRecovered: 0, 
            active: 0, 
            critical: 0, 
            casesPerOneMillion: 0, 
            deathsPerOneMillion: 0, 
            tests: 0, 
            testsPerOneMillion: 0, 
            population: 0, 
            continent: "", 
            oneCasePerPeople: 0, 
            oneDeathPerPeople: 0, 
            oneTestPerPeople: 0, 
            activePerOneMillion: 0, 
            recoveredPerOneMillion: 0, 
            criticalPerOneMillion: 0 }); 
    const [loading, setLoading] = useState(true); 
    
    useEffect(() => { if (pais) { 
        console.log("Fetching data for:", pais); 
        setLoading(true); 
        fetch(`https://disease.sh/v3/covid-19/countries/${pais}`) 
        .then(response => response.json()) 
        .then(data => { 
            console.log("Data fetched:", data); 
            setData(data); setLoading(false); 
            }) 
        .catch(error => { console.error('Error fetching data:', error); 
            setLoading(false); }); } }, [pais]); 
    const diferencia = parseFloat((data.cases - data.recovered) / data.cases * 100).toFixed(2); 
    const position = [data.countryInfo.lat, data.countryInfo.long]; 
    if (loading) { 
        console.log("Loading..."); 
        return <div>Cargando...</div>; } 
    console.log("Rendering map with position:", position); 
    return (
         <MapContainer center={position} zoom={5} scrollWheelZoom={true} key={position}>
   
         
            <TileLayer attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors' url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" /> 
            <Marker position={position}> 
                <Popup> 
                <ol>
                  <li>Muertes:{data.deaths}</li>
                  <li>Casos:{data.cases}</li>
                  <li>Diferencia:{diferencia}</li>
                  <li>Recuperados:{data.recovered}</li>
                </ol>
                    
                </Popup> 
            </Marker> 
     </MapContainer> 
   )}
    const PrimerProyecto = () => {
        const [animate, setAnimate] = useState('carga'); 
        const [pais, setPais] = useState(""); 
        const [inputValue, setInputValue] = useState("");
        const handleInputChange = (e) => setInputValue(e.target.value); 
        const handleButtonClick = () => setPais(inputValue);
         return (
            <div className={`prp ${animate}`}> 
            <h1>Estadísticas de COVID</h1> 
            <input type="text" value={inputValue} onChange={handleInputChange} /> 
            <button onClick={handleButtonClick}>Enviar</button>
            {pais&&<MapComponent className pais={pais} /> }
            </div> ); } 


export default PrimerProyecto;
